package org.fuxin.zcl.test;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import org.apache.http.HttpResponse;
import org.apache.http.util.EntityUtils;

import com.alibaba.fastjson.JSON;
import com.alibaba.fastjson.JSONObject;

public class OCRTest {
	public static void main(String[] args) {
		String host = "http://tysbgpu.market.alicloudapi.com";
	    String path = "/api/predict/ocr_general";
	    String method = "POST";
	    String appcode = "aeb8d4870bfa4442bd63b7134e75db1d";//
	    Map<String, String> headers = new HashMap<String, String>();
	    //最后在header中的格式(中间是英文空格)为Authorization:APPCODE 83359fd73fe94948385f570e3c139105
	    headers.put("Authorization", "APPCODE " + appcode);
	    //根据API的要求，定义相对应的Content-Type
	    headers.put("Content-Type", "application/json; charset=UTF-8");
//	    String base64Str = "";
//	    String base64Str = "";
	    String base64Str = "";
	    Map<String, String> querys = new HashMap<String, String>();
	    JSONConfig config  = new JSONConfig();
	    config.setImage(base64Str);
	    String bodys = JSON.toJSONString(config);
	    System.out.println("The bodys>>>>>>>>>>>"+bodys);
	    try {
	    	/**
	    	* 重要提示如下:
	    	* HttpUtils请从
	    	* https://github.com/aliyun/api-gateway-demo-sign-java/blob/master/src/main/java/com/aliyun/api/gateway/demo/util/HttpUtils.java
	    	* 下载
	    	* 相应的依赖请参照
	    	* https://github.com/aliyun/api-gateway-demo-sign-java/blob/master/pom.xml
	    	*/
	    	HttpResponse response = HttpUtils.doPost(host, path, method, headers, querys, bodys);
	    	System.out.println(response.toString());
	    	JSONObject object = JSONObject.parseObject(EntityUtils.toString(response.getEntity()));
	    	int size = object.getJSONArray("ret").size();
	    	StringBuilder sb = new StringBuilder();
	    	for(int i = 0 ;i < size;i++ ){
	    		sb.append(object.getJSONArray("ret").getJSONObject(i).get("word"));
	    	}
	    	System.out.println(sb.toString());
	    } catch (Exception e) {
	    	e.printStackTrace();
	    }
	}
	
}
class JSONConfig{
	private String image;
	
	public String getImage() {
		return image;
	}

	public void setImage(String image) {
		this.image = image;
	}
	
}
